// Este código deve estar INTEIRO dentro do nó "Code" do n8n

// E-mail do organizador que NUNCA deve aparecer como convidado principal ou em CC.
const ORGANIZER_EMAIL_TO_EXCLUDE = "matheusmafessoli@metodob2all.com.br";

// 1. A FUNÇÃO para filtrar e organizar as informações de UM evento
function filtrarInfoEvento(evento, emailDoOrganizadorParaExcluir) {
  // --- INÍCIO DA FUNÇÃO AUXILIAR DE FORMATAÇÃO DE DATA/HORA (MOVIDA PARA DENTRO) ---
  function formatDateTimeInternal(dateTimeString, isAllDay = false) {
    if (!dateTimeString) {
      return 'Data/Hora não disponível';
    }

    try {
      if (isAllDay) {
        // Para eventos de dia inteiro, formata apenas a data
        const [year, month, day] = dateTimeString.split('-');
        return `${day}/${month} - 00:00`; // Assumindo 00:00 para eventos de dia inteiro
      } else {
        const date = new Date(dateTimeString);
        const dia = String(date.getDate()).padStart(2, '0');
        const mes = String(date.getMonth() + 1).padStart(2, '0');
        const hora = String(date.getHours()).padStart(2, '0');
        const minuto = String(date.getMinutes()).padStart(2, '0');
        return `${dia}/${mes} - ${hora}:${minuto}`;
      }
    } catch (e) {
      return 'Formato de data inválido';
    }
  }
  // --- FIM DA FUNÇÃO AUXILIAR DE FORMATAÇÃO DE DATA/HORA ---

  const nomeEvento = evento.summary || 'Nome do evento não disponível';

  // Usar a função auxiliar para formatar a data/hora
  let inicioEventoFormatado = 'Data/Hora não disponível';
  if (evento.start) {
    if (evento.start.dateTime) {
      inicioEventoFormatado = formatDateTimeInternal(evento.start.dateTime, false);
    } else if (evento.start.date) {
      inicioEventoFormatado = formatDateTimeInternal(evento.start.date, true);
    }
  }

  const convidadosNaoOrganizadores = [];
  if (evento.attendees && Array.isArray(evento.attendees)) {
    evento.attendees.forEach(participante => {
      // Coleta todos os participantes que NÃO são o organizador
      if (participante.email &&
          participante.email !== emailDoOrganizadorParaExcluir &&
          !participante.organizer) { // Mantém a verificação !participante.organizer para robustez
        convidadosNaoOrganizadores.push(participante.displayName || participante.email);
      }
    });
  }

  let convidadoPrincipal = null;
  let emailsEmCopiaCC = [];

  if (convidadosNaoOrganizadores.length > 0) {
    // O primeiro participante da lista se torna o convidado principal
    convidadoPrincipal = convidadosNaoOrganizadores[0];

    // Se houver mais de um participante, os restantes vão para o CC
    if (convidadosNaoOrganizadores.length > 1) {
      emailsEmCopiaCC = convidadosNaoOrganizadores.slice(1); // Pega do segundo em diante
    }
  }

  // Obter o link do Google Meet
  const linkGoogleMeet = evento.hangoutLink || 'Nenhum link do Meet';

  // Lógica para nomeDoLead (mantida do seu código anterior, se for desejado)
  // Se não houver convidadoPrincipal, nomeDoLead será null, o que está correto.
  let nomeDoLead = null;
  if (convidadoPrincipal && typeof convidadoPrincipal === 'string') {
    if (convidadoPrincipal.includes('@')) {
      nomeDoLead = convidadoPrincipal.split('@')[0];
    } else {
      nomeDoLead = convidadoPrincipal;
    }
  }


  // Retorna um objeto com os dados filtrados e organizados
  return {
    nomeDoEvento: nomeEvento,
    dataHoraInicio: inicioEventoFormatado, // Usando a data/hora formatada
    convidadoPrincipal: convidadoPrincipal,
    emailsEmCopiaCC: emailsEmCopiaCC,
    linkGoogleMeet: linkGoogleMeet,
    nomeDoLead: nomeDoLead // Adicionado nomeDoLead de volta, se for o caso
  };
}

// 2. CÓDIGO PRINCIPAL DO NÓ "CODE" DO N8N PARA CADA ITEM DE ENTRADA

const eventoAtual = $json; // Isso acessa o JSON do ITEM ATUAL sendo processado.

// E-mail do organizador que você NÃO quer na lista de participantes.
const emailDoOrganizador = ORGANIZER_EMAIL_TO_EXCLUDE; // Usando a constante definida globalmente

// Chama a função 'filtrarInfoEvento' com o JSON do evento atual
const resultadoProcessado = filtrarInfoEvento(eventoAtual, emailDoOrganizador);

// Retorna DIRETAMENTE o objeto com a propriedade 'json' para o n8n.
// No modo "Run Once for Each Item", o n8n espera um único objeto como retorno.
return { json: resultadoProcessado };

