// Acessa o item de entrada do nó anterior (Webhook)
const webhookData = $input.item.json;

// Acessa o array da transcrição
const transcriptArray = webhookData.body.data.transcript;

// Formata *apenas* as mensagens em uma única string, separando por quebras de linha
// Verifica se a mensagem não é nula antes de adicionar
const formattedMessages = transcriptArray
  .filter(item => item.message !== null) // Filtra mensagens nulas (como as de tool_results)
  .map(item => item.message) // Pega apenas a mensagem de cada item
  .join('\n'); // Junta tudo com quebras de linha

// Acessa o resumo da transcrição (mantendo o que já estava lá)
const transcriptSummary = webhookData.body.data.analysis.transcript_summary;

// Retorna um novo item com as informações formatadas
